	(ans+=solve(dep-1,n-sum))%=mod;
	register int i;
	if(dep^1)sum++;
	for(i=prev;i;i--)siz[dep]=i,dfs(dep+1,i,sum+(i<<1)-3+(i==1)*2);
}
