bool check(int m)
{
	memset(vis,0,sizeof(vis));
	memset(book,0,sizeof(book));
	int cnt=0;
	for(int i=1;i<=m&&a[i]==1;i++)cnt++;
	int nowr=1,nowb=1;
	for(int i=1;i<=m-cnt;i++)
