		while(nowr<=k&&(vis[nowr]||s[nowr]!='r'))nowr++;
		while(nowb<=k&&(vis[nowr]||s[nowb]!='b'||nowb<=nowr))nowb++;
		if(nowr>k||nowb>k)return false;
		vis[nowr]=true;
		vis[nowb]=true;
		book[nowb]=true;
	}
	int tmp=cnt;
	for(int i=1;i<=k&&tmp;i++)
		if(!vis[i]&&s[i]=='r'){
			vis[i]=true;
			tmp--;
		}
	if(tmp)return false;
	int now=cnt+1,num=0;
	for(int i=k;i>=1&&now<=m;i--)
	{
		if(!vis[i])num++;
		if(book[i]){
			num-=a[now]-2;
			if(cnt<0)return false;
			now++;
		}
	}
	return true;
}
int fpow(int a,int b)
{
	int ans=1,t=a;
	while(b)
	{
		if(b&1)ans=1ll*ans*t%Mod;
		t=1ll*t*t%Mod;
		b>>=1;
	}
	return ans;
}
int ans;
int fac[510],inv[510];
void init()
{
	int N=500;
	fac[0]=1;
	for(int i=1;i<=N;i++)
		fac[i]=1ll*fac[i-1]*i%Mod;
	inv[N]=fpow(fac[N],Mod-2);
	for(int i=N;i>=1;i--)
		inv[i-1]=1ll*inv[i]*i%Mod;
	return ;
}
int C(int x,int y)
{
	if(x<0||y<0||x-y<0)return 0;
	return 1ll*fac[x]*inv[y]%Mod*inv[x-y]%Mod;
}
void solve(int m,int len)
{
	int s=0;//block-1
	for(int i=1;i<=m;i++)
		s+=a[i]*2;
	int res=1ll*C(n-len+s,s)*fac[m]%Mod;
	for(int i=1,j;i<=m;i=j)
	{
		j=i;
		while(j<=m&&a[j]==a[i])j++;
		res=1ll*res*inv[j-i]%Mod;
	}
	ans=(ans+res)%Mod;
	return ;
}
bool dfs(int m,int lst,int sum,int len)
{
	if(len>n)return false;
	if(!check(m-1))return false;
	solve(m-1,len);
	for(a[m]=lst;a[m]<=k-sum;a[m]++)
	{
		int tlen=len;
		if(m>1)tlen++;
		if(a[m]==1)tlen++;
		else tlen+=(a[m]-1)*2-1;
		if(!dfs(m+1,a[m],sum+a[m],tlen))
			break;
	}
	return true;
}
