	ll res=fac[cnt],lst=1;
	for(int i=1;i<=cnt;i++) if(f[i]!=f[i-1]&&i>1) res=res*inv[i-lst]%cys,lst=i;
	res=res*inv[cnt+1-lst]%cys;
	return res*C(all+rnum+bnum+wnum+(cnt-num)*2+1,rnum+bnum+wnum+(cnt-num)*2+1)%cys;
}

