	inv[1] = fac[1] = faci[1] = 1;
	for(int i=2; i<MX*3; i++)
	{
		inv[i] = (MOD*MOD - (MOD/i) * inv[MOD%i]) % MOD;
		fac[i] = fac[i-1] * i % MOD;
		faci[i] = faci[i-1] * inv[i] % MOD;
	}
}

ll binom(int x, int y)
{
	if(x<0 || y<0 || x<y) return 0;
	else return fac[x] * faci[y] % MOD * faci[x-y] % MOD;
}

