inline void solve(ri cnt){
	ri i,now,res;
	for(i=1;i<=k;++i) use[i]=0;
	for(i=1;i<=cnt;++i) posr[i]=posb[i]=0;
	now=0;
	for(i=1;i<=k&&now<cnt;++i)
		if(s[i]=='r') use[i]=1,posr[++now]=i;
	if(now<cnt) return;
	now=0;
	for(i=1;i<=cnt&&a[i]>=2;++i){
		for(now=max(now,posr[i]+1);now<=k&&s[now]!='b';++now);
		if(now>k) return;
		posb[i]=now;
		use[now]=1;
		++now;
	}
	now=0;
	for(i=1;i<=cnt&&a[i]>=3;++i){
		res=a[i]-2;
		for(now=max(now,posb[i]+1);now<=k&&res;++now)
			if(!use[now])
				use[now]=1,--res;
		if(res) return;
	}

	ri j,ans=fac[cnt],c1,c2;
	for(i=1;i<=cnt;i=j+1){
		for(j=i;j+1<=cnt&&a[j+1]==a[i];++j);
		ans=(ll)ans*ifac[j-i+1]%mod;
	}
	c1=n+1;
	c2=0;
	for(i=1;i<=cnt;++i){
		if(a[i]>=2)
			c1+=2;
		c2+=a[i]*2;
	} 
	ans=(ll)ans*C(c1,c2)%mod; 
	add(Ans,ans);
}
