int n, K, Fac[333], Rev[333], X[77], C[333][333], ans = 0;

ll powmod(ll x, ll y = P - 2, ll p = P) {
    ll ans = 1;
    while(y) {
        if(y & 1) ans = ans * x % p;
        x = x * x % p;
        y >>= 1;
    }
    return ans;
}

void calc(int sz) {
    int tb = 0, tr = 0;
    for(int i = 1; i <= sz; i++) {
        tr++;
        while(tr <= K && ch[tr] != 'r') tr++;
        if(X[i] > 1) {
            tb = max(tb + 1, tr);
            while(tb <= K && ch[tb] != 'b') tb++;
        }
        if(tr > K || tb > K) return;
    }
    int t = 0, rw = 0, bw = 0;
    for(int i = 1; i <= sz; i++)
        if(X[i] == 1) {
            rw++;
            t++;
        } else {
            bw += X[i] - 1;
            rw += X[i];
            t += X[i] * 2 - 3;
        }
    t = n + 1 - t - sz;
    if(t < 0) return;
    ll tmp = Fac[sz];
    X[sz + 1] = 0; //!!!
    for(int i = 2, lst = 1; i <= sz + 1; i++)
        if(X[i] != X[lst]) {
            tmp = tmp * Rev[i - lst] % P;
            lst = i;
        }
    for(int r = 0; r <= t; r++)
        for(int b = 0; r + b <= t; b++) {
            add(ans, (!rw ? !r : C[r + rw - 1][r]) * (!bw ? !b : C[b + bw - 1][b]) % P * C[sz + (t - r - b)][sz] % P * tmp);
        }
}

void Search(int x, int lst, int tbr) {
    calc(x - 1);
    for(int i = 1; i <= lst && i + tbr <= K; i++) {
        X[x] = i;
        Search(x + 1, i, tbr + i);
    }
}

