			if (now < K) {
				piv[i] = now;
				us[now] = 1;
				now++;
			} else {
				return ;
			}
		}
	}

	now = 0;

	for (int i = sz - 1; i >= 0; --i) {
		int x = vec[i];
		if (x >= 3) {
			x -= 2;
			chmax(now, piv[i] + 1);
			rep(t, x) {
				while (now < K && us[now]) {
					++now;
				}
				if (now < K) {
					us[now] = 1;
					now++;
				} else {
					return ;
				}
			}
		}
	}

	int need = sz - 1;
	int bs = 0;

	for (int x : vec) {
		if (x == 1) {
			++need;
		} else {
			++bs;
			need += x * 2 - 3;
		}
	}

	if (need > N) {
		return ;
	}

	ll coef = fact[sz];
	{

		rep(i, sz) {
			if (i == 0 || vec[i] != vec[i-1]) {
				int c = 0;
				for (int j = i; j < sz; ++j) {
					if (vec[j] == vec[i]) {
						++c;
					} else {
						break;
					}
				}
				coef = coef * ifact[c] % MOD;
			}
		}
	}

	//dump(vec);
	//dump(need);

	coef = coef * comb(N - need + (need + 1 + bs * 2), need + 1 + bs * 2);

	ans = (ans + coef) % MOD;
	++cnt;
}

void gen(int p, int s, V<int>& vec) {
	if (p == K + 1 || s + p + vec.size() > N) {
		calc(vec);
		return ;
	}

	gen(p + 1, s, vec);

	for (; ; ) {
		if (p == 1) {
			++s;
		} else {
			s += p * 2 - 3;
		}
		if (s + vec.size() > N) {
			break;
		}
		vec.pb(p);		
		gen(p + 1, s, vec);
	}
	while (vec.size() && vec.back() == p) {
		vec.pop_back();
	}
}

int main() {
	init();

	cin >> N >> K >> s;
	{
		V<int> vec;
		gen(1, 0, vec);
	}
	//dump(cnt);

	cout << ans << endl;

	return 0;
}

