					if(j+1<=n){
						dp[nxt][j+1]+=dp[cur][j];
						dp[nxt][j+1]%=MOD;
					}
				}else{
					int pl=(group[i]-1)*2-1;
					if(j+pl<=n){
						dp[nxt][j+pl]+=dp[cur][j];
						dp[nxt][j+pl]%=MOD;
					}
					pl=(group[i]-1)*2;
					if(j+pl<=n){
						dp[nxt][j+pl]+=dp[cur][j]*2LL;
						dp[nxt][j+pl]%=MOD;
					}
					pl=(group[i]-1)*2+1;
					if(j+pl<=n){
						dp[nxt][j+pl]+=dp[cur][j];
						dp[nxt][j+pl]%=MOD;
					}
				}
			}
			swap(cur,nxt);
			memset(dp[nxt],0,sizeof(dp[nxt]));
		}
		/*
