	if(x>=mod) x-=mod;
}
int q1[maxn],q2[maxn],suf[maxn],f[maxn][maxn];
bool used[maxn];
void solve(int S1,int S2){
	if(!S1&&!S2) return ++ans,void(0);
	if((S1+S2)*2>n+1 || S1+2*S2>m) return ;
	*q1=*q2=0;
	memset(used,0,sizeof(used));
	int x=0,y=0;
	Rep(i,1,m)
		if(s[i]=='r' && x<S1+S2) q1[++x]=i; else 
		if(s[i]=='b' && y<S2 && y<x) q2[++y]=i;
	if(y!=S2 || x!=S1+S2) return ;
	Rep(i,1,x) used[q1[i]]=true;
	Rep(i,1,y) used[q2[i]]=true;
	Dep(i,m,1) suf[i]=suf[i+1]+(!used[i]);
	Rep(i,1,S2) q2[i] = suf[q2[i]];
//	Rep(i,1,S2) wri(q2[i]);puts("");
	memset(f,0,sizeof(f));
	f[0][0]=1;
	int limit = max(2,n)+1;
	Rep(i,2,limit)Dep(j,S2-1,0)Rep(k,2*j,limit-i*(S2-j)){
		Rep(l,1,min(S2-j,(limit-k)/i)){
			if(k+i*l-2*(j+l) > q2[S2-(j+l)+1]) break;
			add(f[j+l][k+i*l],1ll*ifac[l]*f[j][k]%mod);
		}
	}
	int res = 0;
	Rep(i,0,limit){
		int sum=2*S1+2*i-2*S2-1;
		int par=2*S1+2*i+1;
		if (sum>=0&&sum<=n&&f[S2][i])
			add(res,1ll*f[S2][i]*cal(n-sum,par)%mod);
	}res = 1ll * res * fac[S1+S2] % mod * ifac[S1] % mod;
	add(ans,res);
}
int main(){
//	freopen("cover.in","r",stdin);
//	freopen("cover.out","w",stdout);
	init(); 
	ans = 0;
	Rep(S1,0,n)Rep(S2,0,n)solve(S1,S2);
	writeln(ans);
}

