	x += y;
	if (x >= P) x -= P;
}
int main() {
	read(n), read(m);
	scanf("\n%s", s + 1);
	vis[0][0][0][0] = true;
	for (int i = 1; i <= m; i++) {
		for (int j = 0; j <= i; j++)
		for (int k = 0; k <= j; k++)
		for (int l = k; l <= i; l++) {
			if (!vis[i - 1][j][k][l]) continue;
			vis[i][j][k][l] = true;
			if (s[i] == 'r') {
				vis[i][j + 1][k][l] = true;
				if (k != 0) vis[i][j][k][l + 1] = true;
			} else {
				if (k < j) vis[i][j][k + 1][l + 1] = true;
				if (k != 0) vis[i][j][k][l + 1] = true;
			}
		}
	}
	for (int j = 0; j <= m; j++)
	for (int k = 0; k <= j; k++)
	for (int l = k; l <= m; l++)
		dp[0][j][k][l][0] = vis[m][j][k][l];
	//state: 0 -> w, 1 -> r, 2 -> b*, 3 -> r*
	//j -> segments, k -> with b, l -> cntb
	for (int i = 1, dest = 1, from = 0; i <= n; i++, swap(dest, from)) {
		memset(dp[dest], 0, sizeof(dp[dest]));
		for (int j = 0; j <= m; j++)
		for (int k = 0; k <= m; k++)
		for (int l = k; l <= m; l++) {
			int tmp = dp[from][j][k][l][0];
			update(dp[dest][j][k][l][0], tmp); // 0 + w
			if (j) update(dp[dest][j - 1][k][l][1], tmp); // 0 + r
			if (j && k && l) update(dp[dest][j - 1][k - 1][l - 1][2], tmp); // 0 + b

			tmp = dp[from][j][k][l][1];
			update(dp[dest][j][k][l][0], tmp); // 1 + w
			update(dp[dest][j][k][l][1], tmp); // 1 + r
			if (k && l) update(dp[dest][j][k - 1][l - 1][2], tmp); // 1 + b

			tmp = dp[from][j][k][l][2];
			update(dp[dest][j][k][l][0], tmp); // 2 + w
			update(dp[dest][j][k][l][3], tmp); // 2 + r
			update(dp[dest][j][k][l][2], tmp); // 2 + b

			tmp = dp[from][j][k][l][3];
			update(dp[dest][j][k][l][0], tmp); // 3 + w
			update(dp[dest][j][k][l][3], tmp); // 3 + r
			if (l) update(dp[dest][j][k][l - 1][2], tmp); // 3 + b
		}
	}
	int ans = 0;
	for (int i = 0; i <= 3; i++)
		update(ans, dp[n & 1][0][0][0][i]);
	writeln(ans);
	return 0;
}

