const int N = 105, M = 305, P = 1000000007;
int n, m, C[M][M];
char a[N];
void initC(int n){
	for (register int i = 0; i < n; ++i){
		C[i][0] = C[i][i] = 1;
		for (register int j = 1; j < i; ++j)
			C[i][j] = (C[i - 1][j] + C[i - 1][j - 1]) % P;
	}
}
int cnt, pos[N], suf[N], f[N], dp[N][N], ans;
void DP(int s, int sr){ //  s + sr sr  Rs  B (B, BRB, BRBRB, ...)
	register int ts = s + sr, x = sr + ts - 1, y = 2 * s + 1, lim = n - x;
	if (lim < 0) return;
	for (register int i = 1; i <= m; ++i) suf[i] = 1;
	cnt = 0, *pos = 0;
	for (register int i = 1; i <= m; ++i) //  rb 
		if (a[i] == 'r' && cnt < s) ++cnt, suf[i] = 0;
		else if (a[i] == 'b' && *pos < cnt) pos[++*pos] = i, suf[i] = 0;
	if (*pos < s) return;
	cnt = 0;
	for (register int i = 1; i <= m; ++i) //  rb  r  R
		if (a[i] == 'r' && suf[i] && cnt < sr) ++cnt, suf[i] = 0;
	if (cnt < sr) return;
	suf[m + 1] = 0;
	for (register int i = m; i; --i) suf[i] += suf[i + 1];
	for (register int i = 1; i <= s; ++i) f[i] = suf[pos[s - i + 1]] + i * 2;
	// f[i]  i  i  b 
	for (register int i = 0; i <= s; ++i)
		for (register int j = 0; j <= lim; ++j)
			dp[i][j] = 0;
	dp[0][0] = 1;
	for (register int i = 2; i <= ((lim + 3) >> 1); ++i) // 
		for (register int j = s - 1; ~j; --j) // 
			for (register int k = lim - 1; ~k; --k) // 
				if (dp[j][k])
					for (register int tj = j + 1, len = (i << 1) - 3, tk = k + len; tj <= s; ++tj, tk += len){
						if (tk > lim || (tk + tj * 3) / 2 > f[tj]) break;
						dp[tj][tk] = (dp[tj][tk] + 1ll * dp[j][k] * C[tj][j]) % P;
					}
	for (register int i = 0; i <= lim; ++i, ++x)
		if (dp[s][i]) ans = (ans + 1ll * C[ts][s] * C[n + y][x + y] % P * dp[s][i]) % P;
}
