			if(ch[j]=='r') q[++R]=j;
			else if(L<=R) vis[q[L++]]=vis[j]=1,pos[++cnt]=j;
		if(cnt<i) continue;
		for(int j=1;j<=k;j++)
			if(!vis[j]&&ch[j]=='r') s++;
		pos[i+1]=k+1,up[i+1]=0;
		for(int j=0;j<=s;j++)
		{
			if(j)
			{
				while(vis[r]||ch[r]!='r') r++;
				vis[r]=1;
			}
			for(int t=i;t>0;t--)
			{
				up[t]=up[t+1];
				for(int t_=pos[t+1]-1;t_>pos[t];t_--)
					if(!vis[t_]) up[t]++;
				up[t]++;
			}
			reverse(up+1,up+i+1),solve(n,i,j);
		}
	}
	for(int i=1;i<=n;i++) ans=add(ans,mul(sum[i],C[n-1][i-1]));
	cout<<ans<<endl;
	return 0;
}

