using namespace std;
template<class T> inline bool Cmax(T &x,T y) {return x<y?x=y,1:0;}
typedef long long ll;
const int mod=1e9+7;
const int maxn=1000+5;
const int maxk=70+5;
int n,k;
int an;
int r[maxk],b[maxk];
int fac[maxn];
int inv[maxn];
int num[maxk];
bool vis[maxk];
