inline int add(int x) {return x>=mod?x-mod:x;}
ll power(ll x,ll y)
{
	ll re=1;
	while(y)
	{
		if(y&1) re=re*x%mod;
		x=x*x%mod;
		y>>=1;
	}
	return re;
}
inline int binom(int x,int y)
{
	return (ll)fac[x]*inv[y]%mod*inv[x-y]%mod;
}
bool check()
{
	memset(vis,0,sizeof(vis));
	for(int i=0,j=1;i<a.size();++i)
	{
		while(j<=k&&s[j]!='r') ++j; if(j>k) return 0;
		r[i]=j,vis[j++]=1;
	}
	for(int i=0,j=1;i<a.size();++i) if(a[i]>=2)
	{
		Cmax(j,r[i]+1);
		while(j<=k&&s[j]!='b') ++j; if(j>k) return 0;
		b[i]=j,vis[j++]=1;
	}
	for(int i=0,j=1;i<a.size();++i) if(a[i]>=3)
	{
		Cmax(j,b[i]+1);
		int T=a[i]-2; while(T--)
		{
			while(j<=k&&vis[j]) ++j; if(j>k) return 0;
			vis[j++]=1;
		}
	}
	return 1;
}
void dfs(int rest,int las)
{
	if(check())
	{
		int v=a.size()+1;
		for(int i=0;i<a.size();++i)
		{
			v+=num[a[i]];
			if(a[i]>1) v+=2;
		}
		int re=(ll)binom(rest+v-1,rest)*fac[a.size()]%mod;
		for(int i=0,j;i<a.size();i=j)
		{
			j=i; while(j<a.size()&&a[j]==a[i]) ++j;
			re=(ll)re*inv[j-i]%mod;
		}
		an=add(an+re);
	}
	for(int i=1;i<=las;++i)
	{
		if(num[i]+1>rest) break;
		a.push_back(i);
		dfs(rest-num[i]-1,i);
		a.pop_back();
	}
}
void init(int n)
{
	fac[0]=1;
	for(int i=1;i<=n;++i)
	{
		fac[i]=(ll)fac[i-1]*i%mod;
	}
	inv[n]=inver(fac[n]);
	for(int i=n;i>=1;--i)
	{
		inv[i-1]=(ll)inv[i]*i%mod;
	}
}
int sol()
{
	init(1000);
	an=1;
	num[1]=1;
	num[2]=1;
	for(int i=3;i<=n;++i) num[i]=num[i-1]+2;
	for(int i=1;;++i)
	{
		if(num[i]>n) break;
		a.push_back(i);
		dfs(n-num[i],i);
		a.pop_back();
	}
	return an;
}
int main()
{
	scanf("%d%d",&n,&k);
	scanf("%s",s+1);
	printf("%d\n",sol());
	return 0;
}

