LL ksm(LL a,LL b){
	LL ret=1;
	for(;b;b>>=1,a=a*a%mod)
		if(b&1)ret=a*ret%mod;
	return ret;
}
LL comp(int n,int m){
	return fact[n]*rev[m]%mod*rev[n-m]%mod;
}
void prefact(int n){
	fact[0]=1;
	for(int i=1;i<=n;++i)fact[i]=fact[i-1]*i%mod;
	rev[n]=ksm(fact[n],mod-2);
	for(int i=n;i>=1;--i){
		rev[i-1]=rev[i]*i%mod;
	}
}
int seq[NN][2]={},scnt=0;
LL ans=0;
int len[NN]={};
int pos[NN]={};
bool used[NN]={};
int rcnt=0;
int bcnt=0;
void calc(){
	int tp=0;
	for(int i=1;i<=k;++i)used[i]=0;
	for(int i=1;i<=scnt;++i){
		for(int j=1;j<=seq[i][1];++j){
			len[++tp]=seq[i][0];
			pos[tp]=0;
		}
	}
	int now=1;
	for(int i=1;i<=k;++i){
		if(!used[i]&&s[i]=='r'&&now<=tp&&pos[now]<i){
			--len[now],pos[now]=i,++now,used[i]=1;
		}
	}
	if(now!=tp+1)return ;
	now=1;
	while(now<=tp&&!len[now])++now;
	for(int i=1;i<=k;++i){
		if(!used[i]&&s[i]=='b'&&now<=tp&&pos[now]<i){
			--len[now],pos[now]=i,used[i]=1;
			++now;
			while(now<=tp&&!len[now])++now;
		}
	}
	if(now!=tp+1)return ;
	now=1;
	while(now<=tp&&!len[now])++now;
	for(int i=1;i<=k;++i){
		if(!used[i]&&now<=tp&&pos[now]<i){
			--len[now],pos[now]=i,used[i]=1;
			while(now<=tp&&!len[now])++now;
		}
	}
	if(now!=tp+1)return ;
	int les=tp-1;
	if(tp==0)les=1;
	int seg=tp+1;
	LL ad=fact[tp];
	for(int i=1;i<=scnt;++i){
		ad=ad*rev[seq[i][1]]%mod;
		int l=seq[i][0];
		for(int j=1;j<=seq[i][1];++j){
			if(l==1)les+=1;
			else les+=2*l-3;
			seg+=2*l-1;
		}
	}
	if(les>n)return ;
	//for(int i=1;i<=scnt;++i)cerr<<seq[i][0]<<":"<<seq[i][1]<<endl;
	//cerr<<endl;
	//cerr<<ad<<" "<<seg<<" "<<n-les<<endl;
	ad=ad*comp(n-les+seg-1,n-les)%mod;
	ans=(ans+ad)%mod;
}
int all=0;
void dfs(int rem,int now){
	if(rcnt<all)return ;
	if(now==0){
		calc();
		return ;
	}
	if(bcnt<all)return ;
	for(int i=0;i*now<=rem;++i){
		if(i){
			++scnt;
			seq[scnt][0]=now;
			seq[scnt][1]=i;
			all+=i;
		}
		dfs(rem-i*now,now-1);
		if(i){
			--scnt;
			all-=i;
		}
	}
}
