#include <bits/stdc++.h>
using namespace std;
const int N = 75;
const int mod = 1000000007;
int n, m, posr[N], cr, a[N], fact[301], ifact[301], inv[301];
char S[N];
int ans;
inline void Init() {
	inv[1] = 1;
	for (int i = 2; i <= 300; i++) inv[i] = 1ll * (mod - mod / i) * inv[mod % i] % mod;
	fact[0] = ifact[0] = 1;
	for (int i = 1; i <= 300; i++) {
		fact[i] = 1ll * fact[i - 1] * i % mod;
		ifact[i] = 1ll * ifact[i - 1] * inv[i] % mod;
	}
}
inline void calc(int cnt, int sum) {
	if (!cnt) { ans = (ans + 1) % mod; return; }
	if (cr < cnt) return;
	int R[N], B[N], vis[N];
	for (int i = 1; i <= m; i++) vis[i] = 0;
	int fir1 = cnt + 1;
	for (int i = 1, posb = 1; i <= cnt; i++) {
		if (a[i] == 1) {
			fir1 = i;
			while (i <= cnt) {
				R[i] = B[i] = posr[i];
				vis[R[i]] = 1;
				i++;
			}
			break;
		}
		if (posb < posr[i]) posb = posr[i] + 1;
		while (posb <= m && S[posb] != 'b') posb++;
		if (posb > m) return;
		R[i] = posr[i];
		B[i] = posb;
		vis[R[i]] = vis[B[i]] = 1;
		posb++;
	}
	for (int i = fir1 - 1, j = m, ss = 0, ps = 0; i; i--) {
		while (j > B[i]) {
			if (!vis[j]) ss++;
			j--;
		}
		if (ss - ps < a[i] - 2) return;
		ps += a[i] - 2;
	}
	int res = fact[cnt], cp = 1;
	for (int i = 1, j = 0; i <= cnt; i++) {
		j++;
		cp += a[i] * 2;
		if (i == cnt || a[i] != a[i + 1]) {
			res = 1ll * res * ifact[j] % mod;
			j = 0;
		}
	}
	res = 1ll * res * fact[n - sum + cp] % mod * ifact[cp - 1] % mod * ifact[n - sum + 1] % mod;
	ans = (ans + res) % mod;
}
void go(int d, int mx, int sum) {
	calc(d - 1, sum);
	if (sum + 1 <= n) {
		a[d] = 1;
		go(d + 1, 1, sum + 2);
	}
	for (int i = 2; i <= mx; i++) {
		if (sum + 2 * i - 3 <= n) {
			a[d] = i;
			go(d + 1, i, sum + 2 * i - 2);
		}
	}
}
int main() {
	Init();
	scanf("%d%d %s", &n, &m, S + 1);
	cr = 0;
	for (int i = 1; i <= m; i++) {
		if (S[i] == 'r') posr[++cr] = i;
	}
	go(1, (n + 3) >> 1, 0);
	printf("%d\n", ans);
	return 0;
}

