struct Comb {
    int N;
    vector<mint> fac, ifac, inv;

    Comb(int _n=1) {
        resize(2);
        fac[0] = fac[1] = ifac[0] = ifac[1] = inv[1] = 1;
        N = 1; grow(_n);
    }

    void resize(int _s) {
        fac.resize(_s); ifac.resize(_s); inv.resize(_s);
    }
    bool grow(int _n) {
        if (_n <= N) return false;
        swap(_n, N); resize(N+1);
        for (int i = _n+1; i <= N; i++) {
            fac[i] = fac[i-1] * i;
            inv[i] = (-MOD/i) * inv[MOD%i];
            ifac[i] = ifac[i-1] * inv[i];
        }
        return true;
    }
    // perm
    mint a(int n, int k) {
        if (n < k || k < 0) return 0;
        if (n > N) grow(n);
        return fac[n] * ifac[n-k];
    }
    // choose
    mint c(int n, int k) {
        if (n < k || k < 0) return 0;
        if (n > N) grow(n);
        return fac[n] * ifac[n-k] * ifac[k];
    }
    // sum x = s, x > 0
    mint p(int s, int k) {
        return c(s-1, k-1);
    }
    // sum x = s, x >= 0
    mint z(int s, int k) {
        return p(s+k, k);
    }
    // \E Y-X >= m
    mint hit(int n, int k, int m=1) {
        if (m <= k-n) return c(n+k, n);
        return c(n+k, k-m);
    }
    // catalan trapezoids C_m(n, k)
    // \A Y-X < m,  =c(n+k,n) - hit
    mint cat(int n, int k, int m=1) {
        if (m > k) return c(n+k, n);
        if (m <= k-n) return 0;
        return c(n+k,n) - c(n+k, k-m);
    }
} c(150);

mint res;
int n,k;
string s;
bool trk[77];
int8_t pos[77];
int8_t cnt[77];

bool check(const vector<int>& ip, int sum) {
    memset(trk, 0, sizeof trk);
    memset(pos, -1, sizeof pos);
    int m = ip.size();
    for (int i = m-1; i >= 0; i--) {
        for (int j = 0; j < k; j++) {
            if (s[j]=='r' && !trk[j]) {
                pos[i] = j; trk[j] = true;
                break;
            }
        }
        if (pos[i] == -1) return false;
    }
    memset(cnt, 0x01, sizeof cnt);
    for (int i = m-1; i >= 0; i--) {
        if (ip[i] == 1) continue;
        for (int j = pos[i]+1; j < k; j++) {
            if (s[j]=='b' && !trk[j]) {
                cnt[i]++;
                pos[i]=j; trk[j] = true;
                break;
            }
        }
        if (cnt[i] != 2) return false;
    }
    for (int i = m-1; i >= 0; i--) {
        for (int j = pos[i]+1; ip[i] > cnt[i] && j < k; j++) {
            if (!trk[j]) {
                cnt[i]++;
                pos[i] = j; trk[j] = true;
            }
        }
        //cout << i << ','<<cnt[i]<<'\n';
        if (ip[i] != (int)cnt[i]) return false;
    }

    mint coeff = c.fac[m];
    int las = 0, len = 0;
    for (const int& x: ip) {
        if (x == las) len++;
        else {
            coeff *= c.ifac[len];
            len = 1;
            las = x;
        }
    }coeff *= c.ifac[len];

    int zero = 0;
    zero += 2;
    for (const int& x: ip) {
        if (x > 1) zero+=2;
    }

    if (sum == 0) {
        res += 1; return true;
    }
    //dbg(ip, sum);
    //cout << coeff << '\n';
    res += c.p(n+zero, sum+zero) * coeff;
    //cout << res << "\n";
    return true;
}

void dfs(vector<int>& ip, int sum) {
    check(ip, sum);
    //dbg(ip);
    int sta = ip.empty()? 1 : ip.back();
    for (int i = sta; i <= n; i++) {
        int nex_sum = sum + max(2*i-3, 1) + (ip.empty()?0:1);
        if (nex_sum > n) break;
        ip.emplace_back(i);
        dfs(ip, nex_sum);
        ip.pop_back();
    }
}

void solve() {
    cin >> n >> k;
    cin >> s;
    vector<int> ip;
    dfs(ip, 0);
    cout << res;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    solve();
    cout << endl;
}

