const int maxn = 255;
const int mod = 1e9+7;
int n,m;
char s[maxn];
int ans,fac[maxn],ifac[maxn];
inline int qpow(int a,int b){
	int ans=1;
	for(;b;b>>=1,a=1ll*a*a%mod)
		if(b&1) ans=1ll*ans*a%mod;
	return ans;
}
void init(){
	n = read(),m = read();
	scanf("%s",s+1);
	fac[0] = 1;rep(i,1,maxn) fac[i] = 1ll * fac[i-1] * i % mod;
	ifac[maxn-1] = qpow(fac[maxn-1],mod-2);
	Dep(i,maxn-2,0) ifac[i] = 1ll * ifac[i+1] * (i+1) % mod;
	assert(ifac[0]==1);
}
inline int C(int n,int m){
	return 1ll * fac[n] * ifac[m] % mod * ifac[n-m] % mod;
}
#define cal(x,y) C((x)+(y)-1,(y)-1)
int g[maxn],a[maxn];
void calc(int w,int tot){
	Rep(i,1,tot) a[i] = g[tot-i+1];a[tot+1]=0;
	int lim=0;while(lim<tot && a[lim+1]>1) ++lim;
	int i,j,k,l,t;
	for(i=1,k=1,l=1,t=0;i<=m;++i){
		if(s[i]=='r' && k<=tot) 
		k++; else
		if(s[i]=='b' && l<k && l<=lim) t+=a[l]-2,l++; else
		if(t) t--; 
	}
	if(k<=tot || l<=lim || t) return ;
	int pos=tot+1,res=fac[tot];
	for(i=1;i<=tot;i=j+1){
		for(j=i;a[j+1]==a[i];++j);
		res=1ll*res*ifac[j-i+1]%mod;
		pos+=(j-i+1)*(2*a[i]-1);
	}
	ans=(ans+1ll*res*cal(w,pos))%mod;
}
void dfs(int w,int z,int last){
	calc(w,z-1);
	if(last==1&&w-1>=0) g[z]=1,dfs(w-1-(z>1),z+1,1); 
	for(int i=max(2,last);w-(i-1)-(i-2)>=0;++i){
		g[z] = i,dfs(w-(i-1)-(i-2)-(z>1),z+1,i);
	}
	g[z] = 0;
}
int main(){
	ans=0;
	init(); 
	dfs(n,1,1);
	writeln(ans);
}

