const int INF = INT_MAX / 2;
const int MOD = 1e9 + 7;
const ll e18 = 1e18;

ll dp[72][71][71][36], C[100][100], unko[100][100];

ll H(int x, int y) {
	if (!y) return !x;
	return C[x - 1][y - 1];
}

int main() {
	rep(x, 100) {
		C[x][0] = 1;
		for (int y = 1; y <= x; y++)
			C[x][y] = (C[x - 1][y - 1] + C[x - 1][y]) % MOD;
	}
	for (int d = 1; d < 100; d++)
		for (int b = 1; b <= (d + 1) / 2; b++)
			for (int r = b - 1; r <= b + 1; r++)
				if (b + r <= d) {
					unko[d][b] = (unko[d][b] + H(d, b + r)) % MOD;
					if (b == r) unko[d][b] = (unko[d][b] + H(d, b + r)) % MOD;
				}
	int N, M; cin >> N >> M;
	string s; cin >> s;
	int R = 0, L = 0;
	rep(j, M) {
		if (s[j] == 'r') R++;
		if (s[j] == "rb"[L % 2]) L++;
	}
	L /= 2;
	dp[0][0][0][0] = 1;
	rep(i, N + 1) rep(r, R + 1) rep(s, M + 1) rep(l, L + 1) {
		ll z = dp[i][r][s][l];
		if (!z) continue;
		dp[i + 1][r][s][l] = (dp[i + 1][r][s][l] + z) % MOD;
		if (r == R) continue;
		for (int d = 1; i + d <= N; d++) {
			int j = i + d + 1;
			if (s + 1 <= M)
				dp[j][r + 1][s + 1][l] = (dp[j][r + 1][s + 1][l] + z) % MOD;
			if (l == L) continue;
			for (int b = 1; b <= (d + 1) / 2 && s + b + 1 <= M; b++)
				dp[j][r + 1][s + b + 1][l + 1] = (dp[j][r + 1][s + b + 1][l + 1] + z * unko[d][b]) % MOD;
		}
	}
	ll ans = 0;
	rep(r, R + 1) rep(s, M + 1) rep(l, L + 1)
		ans = (ans + dp[N + 1][r][s][l]) % MOD;
	cout << ans << endl;
}

#include <bits/stdc++.h>
using namespace std;

const int P = 1000000007;
int fac[150], ifac[150], ans, n, k;
bool del[75];
vector<int> f, r;
char s[75];

int power(int a, int x) {
	int ans = 1;
	for (; x; x >>= 1, a = 1ll * a * a % P)
		if (x & 1) ans = 1ll * ans * a % P;
	return ans;
}

bool check() {
	fill(del + 1, del + 1 + k, false);
	for (int i = 0; i < f.size(); ++i) del[r[i]] = true;
	vector<int> b(f.size());
	for (int i = f.size() - 1; i >= 0; --i)
		if (f[i] >= 2) {
			int j;
			for (j = r[f.size() - 1 - i] + 1; (del[j] || s[j] == 'r') && j <= k; ++j);
			if (j > k) return false;
			b[i] = j;
			del[j] = true;
		}
	for (int i = f.size() - 1; i >= 0; --i)
		if (f[i] >= 3) {
			int cnt = f[i] - 2;
			for (int j = b[i] + 1; cnt && j <= k; ++j)
				if (!del[j]) del[j] = true, --cnt;
			if (cnt) return false;
		}
	return true;
}

void dfs(int lst, int sum) {
	if (f.size() > r.size()) return;
	if (check()) {
		int s = n + 1, c = fac[f.size()], s1 = 0;
		for (int i = 0, j; i < f.size(); i = j) {
			for (j = i + 1; j < f.size() && f[j] == f[i]; ++j);
			c = 1ll * c * ifac[j - i] % P;
			if (f[i] > 1) s += 2 * (j - i);
			s1 += 2 * f[i] * (j - i);
		}
		ans = (ans + 1ll * c * fac[s] % P * ifac[s1] % P * ifac[s - s1]) % P;
	}
	for (int i = lst; (sum + max(1, i - 1)) * 2 <= n + 1; ++i) f.push_back(i), dfs(i, sum + max(1, i - 1)), f.pop_back();
}

int main() {
	scanf("%d%d%s", &n, &k, s + 1);
	for (int i = 1; i <= k; ++i)
		if (s[i] == 'r') r.push_back(i);
	for (int i = fac[0] = 1; i <= 2 * n + 5; ++i) fac[i] = 1ll * fac[i - 1] * i % P;
	ifac[2 * n + 5] = power(fac[2 * n + 5], P - 2);
	for (int i = 2 * n + 5; i; --i) ifac[i - 1] = 1ll * ifac[i] * i % P;
	dfs(1, 0);
	printf("%d\n", ans);
	return 0;
}

