using namespace std;
char s[75];int n,K,ans,a[75],p[205],inv[205];
inline int C(int n,int m){return 1LL*p[n]*inv[m]%mod*inv[n-m]%mod;}
void Check(int num,int sum)
{
	int pos=num+1,res=p[num],rnum=0,leftr=0,nxt=1,left=0;
	for(int i=1;i<=K;++i)
		if(s[i]=='r') rnum<num?leftr+=(a[++rnum]>1):(left?--left:0);
		else leftr?(--leftr,left+=a[nxt]-2,++nxt):(left?--left:0);
	if(left||rnum<num||leftr) return;
	for(int i=1,j;i<=num;i=j+1)
	{
		for(j=i;j<num&&a[j+1]==a[i];++j);
		res=1LL*res*inv[j-i+1]%mod;
		pos+=(j-i+1)*(a[i]*2-1);
	}
	ans=(ans+1LL*res*C(n-sum+pos-1,pos-1))%mod;
}
void dfs(int x,int num,int sum)
{
	if(sum>n) return;Check(num,sum);
	for(int i=x;i;--i) a[num+1]=i,dfs(i,num+1,sum+(i>1?i*2-3:1)+(num>0));
}
int main()
{
	scanf("%d%d%s",&n,&K,s+1);
	p[0]=inv[0]=p[1]=inv[1]=1;
	for(int i=2;i<=200;++i) p[i]=1LL*p[i-1]*i%mod,inv[i]=1LL*(mod-mod/i)*inv[mod%i]%mod;
	for(int i=2;i<=200;++i) inv[i]=1LL*inv[i-1]*inv[i]%mod; 
	dfs(n,0,0);cout<<ans;
	return 0;
}

