ll Dodaj(ll a, ll b) {
  a += b;
  if (a >= mod) a -= mod;
  return a;
}

ll Mnoz(ll a, ll b) {
  return (a * b) % mod;
}

ll dwumian[nax][nax];
ll sposoby[nax][nax];

ll C(int n, int k) {
  if (0 <= k and k <= n) {
    assert(0 <= n and n < nax);
    return dwumian[n][k];
  }
  return 0;
}

void InitSposoby() {
  for (int n = 0; n < nax; n++) {
    dwumian[n][0] = dwumian[n][n] = 1;
    for (int k = 1; k < n; k++) {
      dwumian[n][k] = Dodaj(dwumian[n - 1][k - 1], dwumian[n - 1][k]);
    }
  }

  for (int len = 0; len < nax; len++) {
    sposoby[len][0] = 1;
    sposoby[len][1] = (len * (len + 1)) / 2;
  }
  for (int cntb = 2; cntb < nax; cntb++) {
    for (int len = 0; len < nax; len++) {
      for (int i = 0; i < len; i++) {
        for (int j = i + 1; j < len; j++) {
          sposoby[len][cntb] = Dodaj(sposoby[len][cntb], sposoby[i][cntb - 1]);
        }
      }
    }
  }
}

bool NewSizes(const vector<int>& sizes, int cnt, vector<int>& res) {
  res = sizes;
  int zostalo = cnt;
  for (int i = (int) res.size() - 1; i >= 0; i--) {
    zostalo -= res[i];
    if (zostalo <= 0) {
      for (int j = i; cnt > 0; j++) {
        const int ile = min(cnt, res[j]);
        res[j] -= ile;
        cnt -= ile;
      }
      if (i > 0) {
        res[i - 1] += res[i];
      }
      res.erase(res.begin() + i);
      return true;
    }
  }
  return false;
}

ll Dp(int n, const vector<int>& sizes);
ll Dp_(int n, const vector<int>& sizes) {
  ll wynik = 0;
  for (int len = 1; len <= n; len++) {
    for (int cnt_b = 1; cnt_b <= (len + 1) / 2; cnt_b++) {
      if (sposoby[len][cnt_b]) {
        vector<int> uloz;
        if (NewSizes(sizes, cnt_b - 1, uloz)) {
          wynik = Dodaj(wynik, Mnoz(sposoby[len][cnt_b], Dp(n - len, uloz)));
        } else {
          break;
        }
      }
    }
  }
  //debug() << "Dp(" imie(n) imie(sizes) ") = " << wynik;
  return wynik;
}

map<vector<int>, ll> dp_memo[nax];
ll Dp(int n, const vector<int>& sizes) {
  assert(0 <= n and n < nax);
  if (n == 0) {
    if (sizes.empty()) {
      return 1;
    }
    return 0;
  }
  auto res = dp_memo[n].emplace(sizes, 0ll);
  if (res.second) {
    res.first->second = Dp_(n, sizes);
  }
  return res.first->second;
}

