void calc(int num)
{
	//check
	for(int i=1;i<=k;i++)
		vis[i]=false;
	int cur=nxt[1][0],cnt=0;
	for(int i=1;i<=num;i++)
	{
		if(f[i]==1&&cnt==0)
			cnt=num-i+1;
		if(cur>k)return ;
		vis[cur]=true;h[i]=cur;
		cur=nxt[cur+1][0];
	}
	cur=nxt[1][1];
	for(int i=1;i<=num-cnt;i++)
	{
		while(cur<=k&&(cur<h[i]||vis[cur]))
			cur=nxt[cur+1][1];
		if(cur>k)return ;
		vis[cur]=true;
		g[i]=cur;
	}
	int now=num-cnt;
	cur=f[now];
	while(cur<=2&&now>0){
		now--;
		cur=f[now];
	}
	for(int i=k;i>=1;i--)
		if(!vis[i]){
			if(now<=0)break;
			if(i<g[i])return ;
			vis[i]=true;
			cur--;
			while(cur<=2&&now>0){
				now--;
				cur=f[now];
			}
		}
	if(now>0)return ;
	int sum=fac[num];cur=0;
	int cntb=0,cntr=0,cntw=num-1;
	for(int i=1;i<=num;i++)
	{
		if(f[i]==1)cntr++;
		else cntr+=f[i]-2,cntb+=f[i]-1;
		if(i==1||f[i]==f[i-1])cur++;
		else{
			sum=(long long)sum*inv[cur]%Mod;
			cur=1;
		}
	}
	sum=(long long)sum*inv[cur]%Mod;
	int cntB=(num-cnt)*2+1;//rbr->2(num-cnt)  ...rw->1
	if(cntb+cntr+cntw>n)return ;
	sum=(long long)sum*C(n+cntB,cntb+cntr+cntw+cntB)%Mod;
//	for(int i=1;i<=num;i++)
//		printf("%d ",f[i]);
//	printf(":%d\n",sum);
	ans=(ans+sum)%Mod;
	return ;
}
