const int mod = 1000000007;

int qpow(int base,int tms)
{
	int tmp = 1;
	while(tms)
	{
		if(tms & 1) tmp = tmp * 1ll * base % mod;
		base = base * 1ll * base % mod;
		tms >>= 1;
	}
	return tmp;
}

bool chk(int cn)
{
	if(cn > cnt) return false;
	static int sum[75]; memset(sum,0,sizeof(sum));
	for(int i = cn;i >= 1; -- i)
	{
		if(!pos[i]) return false;
		sum[pos[i]] ++;
		if(ln[cn - i + 1] >= 2)
		{
			if(!mat[pos[i]]) return false;
			sum[mat[pos[i]]] += ln[cn - i + 1] - 1;
		}
	}
	for(int i = k;i >= 1; -- i)
	{
		sum[i] += sum[i + 1];
		if(sum[i] > k - i + 1) return false;
	}
	return true;
}

bool dfs(int mi,int cnt,int mus)
{
	if(!chk(cnt)) return false;
	int k = cnt + 1;
	for(int i = 1;i <= cnt; ++ i)
		k += 2 * ln[i] - 1;
	int as = C[n - mus + k - 1][k - 1];
	if(k > 500) cerr << k << endl; 
	int p = 0;
	for(int i = 1;i <= cnt; ++ i)
	{
		if(ln[i] != ln[i - 1])
			as = as * 1ll * inv[p] % mod,p = 0;
		p ++;
	}
	as = as * 1ll * inv[p] % mod;
	as = as * 1ll * fact[cnt] % mod;
	ans = (ans + as) % mod; 
	for(int i = mi;i <= 70; ++ i)
	{
		ln[cnt + 1] = i;
		int nw = mus;
		if(cnt) nw ++;
		nw += i <= 2 ? 1 : 2 * i - 3;
		if(nw > n) break;
		if(!dfs(i,cnt + 1,nw)) break;
	}
	return true;
}

