void mod_inverse(int n, vector<long long>& inv, int mod)
{
    inv.assign(n+1, -1);
    inv[1] = 1;
    for(int i=2; i<=n; ++i)
        inv[i] = inv[mod % i] * (mod - mod / i) % mod;
}

class FactorialCalculation
{
private:
    const int mod;
    vector<long long> factorial;
    vector<long long> invFactorial;
public:
    FactorialCalculation(int n, int mod) : mod(mod)
    {
        factorial.resize(n+1, 1);
        invFactorial.resize(n+1, 1);
        vector<long long> inv;
        mod_inverse(n, inv, mod);
        for(int i=1; i<=n; ++i){
            factorial[i] = factorial[i-1] * i % mod;
            invFactorial[i] = invFactorial[i-1] * inv[i] % mod;
        }
    }
    long long getFactorial(int n){
        return factorial[n];
    }
    long long getInvFactorial(int n){
        return invFactorial[n];
    }
    long long getPermutation(int n, int r){
        if(n < r)
            return 0;
        return factorial[n] * invFactorial[n-r] % mod;
    }
    long long getCombination(int n, int r){
        if(n < r)
            return 0;
        return getPermutation(n, r) * invFactorial[r] % mod;
    }
    long long getHomogeneous(int n, int r){
        return getCombination(n+r-1, r);
    }
};

const long long MOD = 1000000007;
FactorialCalculation fc(200, MOD);

int ballNum, opeNum; // 
string opeStr; // 

long long calc(const vector<int>& v)
{
    int n = v.size();
    int cnt = 1;
    int sum = 1;
    long long ans = 1;
    for(int i=0; i<n; ++i){
        if(i < n - 1 && v[i] == v[i+1]){
            ++ cnt;
        }
        else{
            ans *= fc.getHomogeneous(sum, cnt);
            ans %= MOD;
            sum += cnt;
            cnt = 1;
        }
    }
    return ans;
}

long long solve(const vector<int>& v)
{
    int size = v.size();
    vector<int> index(size+1, -1);
    vector<bool> check(opeNum, false);
    for(int i=size-1; i>=0; --i){
        index[i] = index[i+1] + 1;
        while(index[i] < opeNum && opeStr[index[i]] != 'r')
            ++ index[i];
        if(index[i] == opeNum)
            return -1;
        check[index[i]] = true;
    }
    for(int i=size-1; i>=0; --i){
        if(v[i] > 1){
            while(index[i] < opeNum && opeStr[index[i]] != 'b')
                ++ index[i];
            if(index[i] == opeNum)
                return -1;
            check[index[i]] = true;
        }
    }

    int k = opeNum - 1;
    for(int i=0; i<size; ++i){
        int x = v[i] - 2;
        while(x > 0 && k > index[i]){
            if(!check[k])
                -- x;
            -- k;
        }
        if(x > 0)
            return -1;
    }

    int cnt = v.size() + 1;
    int rest = ballNum - (v.size() - 1);
    for(int i=0; i<size; ++i){
        cnt += v[i] * 2 - 1;
        rest -= max(1, v[i] * 2 - 3);
    }
    if(rest < 0)
        return -1;

    long long ans = fc.getHomogeneous(cnt, rest);
    ans *= calc(v);
    ans %= MOD;
    return ans;
}

long long dfs(int a, vector<int> v)
{
    long long ans = 0;
    vector<int> v2 = v;
    for(;;){
        v.push_back(a);
        long long tmp = solve(v);
        if(tmp == -1)
            break;
        ans += tmp;
        ans %= MOD;
        ans += dfs(a+1, v2);
        ans %= MOD;
        v2.push_back(a);
    }
    return ans;
}

