	for(int i=1;i<=top;i++) tot+=stk[i];
	for(int i=0;i<=res;i++){
		if(tot+top+i*2>n+1) break;
		if(!check(i)) break;
		fas=fac[num+i]*val%md*inv[i]%md;
		(sum[tot+top+i*2][top]+=fas)%=md;
//		printf("%d %d\n",tot+top+i*2,top);
	}
}
