int dp[Nmax][Nmax][Nmax];
vi Sigma(vi num){
	int p=num.size(),m=num.back();
	ZERO(dp);
	dp[0][0][0]=1;
	REP(y,m+1){
		for(int x=p;x>=0;x--){
			REP(sm,m+1){
				int cur=sm;
				FOR(z,x+1,p+1){
					cur+=y;
					if(cur>num[z-1])break;
					add(dp[z][y][cur],dp[x][y][sm]*Choose(p-x,z-x));
				}
			}
		}
		if(y<m)
		REP(x,p+1)
			REP(sm,m+1)
				add(dp[x][y+1][sm],dp[x][y][sm]);
	}
	return vi(dp[p][m],dp[p][m]+m+1);
}

int hog[Nmax];

int semiBuf[Nmax][Nmax];
void Semiexp(int u,int p,int w){
	if(u==0){
		add(hog[1],w);
	}else{
		add(hog[u+p-1],w);
		add(hog[u+p],w*2);
		add(hog[u+p+1],w);
	}
}

void Wafrelka(int p,int q){
	vi z(k,0);
	{
		int cnt=0;
		REP(i,k)
			if(t[i]>=p*2&&s[i]=='r'&&cnt<q){
				z[i]=1;
				cnt++;
			}
		if(cnt<q)return;
	}
	vi num(p,0);
	{
		int j=0;
		for(int i=k-1;i>=0;i--)if(j<p){
			if(t[i]>=p*2){
				if(z[i]==0)
					num[j]++;
			}else if(t[i]%2){
				j++;
			}
		}
	}
	FOR(i,1,p)
		num[i]+=num[i-1];
	if(p==0)num.PB(0);
	vi x=Sigma(num);
	REP(i,x.size()){
		REP(j,p*2+1){
			int w=Choose(p+q,p);
			mult(w,x[i]);
			mult(w,Choose(p*2,j));
			int used=p+i*2+j+q;
			if(used+(p+q-1)<=n)
				add(semiBuf[used][p+q],w);
		}
	}
}

