	for (int i = 0; i < top; i++) cnt += (bool)stk[i];
	if (cnt > vec.size()) return false;
	for (int i = 0; i < m; i++) vis[i] = true;
	for (int i = 0; i < cnt; i++) {
		vis[vec[i].first] = vis[vec[i].second] = false;
		pos[i] = vec[i].second;
	}

	int b = 0, r = 0, j = m - 1;
	for (int i = cnt - 1; i >= 0; i--) {
		for (; j >= pos[i]; j--) {
			if (vis[j] && str[j] == 'b') b++;
			if (vis[j] && str[j] == 'r') r++;
		}
		if (b + r < stk[top - 1 - i] - 1) return false;
		if (b >= stk[top - 1 - i] - 1) b -= stk[top - 1 - i] - 1;
		else r = b + r - stk[top - 1 - i] + 1, b = 0; 
	}
	for (; j >= 0; j--) {
		if (vis[j] && str[j] == 'b') b++;
		if (vis[j] && str[j] == 'r') r++;
	}
	return r >= top - cnt;
}
long long solve () {
	if (!check()) return 0ll;
	long long res = 1ll;
	for (int i = 0; i <= n; i++) tmp[i] = 0;
	for (int i = 0; i < top; i++) tmp[stk[i]]++;
	for (int i = 0, j = 0; i <= n; i++) {
		j += tmp[i];
		res = res * c[j][tmp[i]] % mod;
	}

	int num1 = 0, num2 = 0;
	for (int i = 0; i < top; i++) {
		if (stk[i]) num1 += 2 * stk[i] - 1, num2 += 2;
		else num1++;
	}
	if (top) num1 += top - 1, num2 += 2;
	else num1++;
	res = res * c[n + num2 - 1][num1 + num2 - 1] % mod;
	return res;
}

