	for(register int i=k;i;i--)
	{
		if(!vis[i])R++;
		if(R>=ss)if(!vis[i])if(str[i]=='r')return cover(i,ss),1;
	}return 0;
}
bool valid(int T)
{
	if(!T)return 1;
	register int i;
	for(i=1;i<=k;i++)vis[i]=0;
	for(i=1;i<=T;i++)
		if(siz[i]==1)
			if(!eraser())return 0;
	for(i=T;i;i--)
		if(siz[i]>1)
			if(!elim(siz[i]))return 0;
	return 1;
}
ll concen(int T)
{
	register int i,ii;
	ll ret=1,G=T;
	for(i=1;i<=T;i=ii+1)
	{
		for(ii=i;ii<T&&siz[ii]==siz[ii+1];ii++);
		(ret*=C[G][ii-i+1])%=mod,G-=ii-i+1;
	}return ret;
}
ll solve(int T,int res)
{
	if(!T)return 1;
	register int i;
	ll SS=T+1;
	for(i=1;i<=T;i++)SS+=2*siz[i]-1;
	return C[SS+res-1][res]*concen(T);
}
void dfs(int dep=1,int lim=k,int prev=k,int sum=0)
{
	if(sum>n)return;
	if(!valid(dep-1))return;
	(ans+=solve(dep-1,n-sum))%=mod;
	register int i;
	if(lim>n)lim=n+1;
	if(prev>lim)prev=lim;
	if(dep^1)sum++;
	for(i=prev;i;i--)siz[dep]=i,dfs(dep+1,lim-i,i,sum+(i<<1)-3+(i==1)*2);
}
