class Solve{
    public Solve(){}
    Fact F;
    StringBuilder sb;
    ReadData re;
    public static int Main(){
        new Solve().Run();
        return 0;
    }
    void Run(){
        sb = new StringBuilder();
        re = new ReadData();
        Calc();
        Console.Write(sb.ToString());
    }
    void Calc(){
        int N = re.i();
        int K = re.i();
        string S = re.s();
        List<List<int>>[,] Div = new List<List<int>>[K+1,K+1];
        Div[0,0] = new List<List<int>>();
        Div[0,0].Add(new List<int>());
        for(int i=0;i<=K;i++){
            for(int j=0;j<=K;j++){
                Div[i,j] = new List<List<int>>();
            }
        }
        Div[0,0].Add(new List<int>());
        for(int i=1;i<=K;i++){
            for(int j=1;j<=K;j++){
                for(int k=0;k<Div[i-1,j-1].Count;k++){
                    List<int> L = new List<int>();
                    List<int> cL = Div[i-1,j-1][k];
                    L.Add(1);
                    for(int l=0;l<cL.Count;l++){
                        L.Add(cL[l]);
                    }
                    Div[i,j].Add(L);
                }
                if(j >= i){
                    for(int k=0;k<Div[i,j-i].Count;k++){
                        List<int> L = new List<int>();
                        List<int> cL = Div[i,j-i][k];
                        for(int l=0;l<cL.Count;l++){
                            L.Add(cL[l]+1);
                        }
                        Div[i,j].Add(L);
                    }
                }
            }
        }
        long count = 0;
        F = new Fact(1000);
        for(int i=0;i<=K;i++){
            for(int j=0;j<=K;j++){
                for(int k=0;k<Div[i,j].Count;k++){
                    if(Possible(Div[i,j][k],S)){
                        count += Querry(Div[i,j][k],N);
                    }
                }
            }
        }
        count %= Define.mod;
        sb.Append(count+"\n");
    }
    long Querry(List<int> L,int N){
        if(L.Count == 0){
            return 1;
        }
        int a = 0;
        int b = 0;
        for(int i=0;i<L.Count;i++){
            if(L[i] == 1){
                a += 1;
            }
            else{
                a += L[i]*2-3;
                b += 2;
            }
        }
        a += L.Count-1;
        b += 2;
        if(N < a){
            return 0;
        }
        long ans = F.GetConv(N+b-1,a+b-1);
        ans *= F.f[L.Count];
        ans %= Define.mod;
        int c = 1;
        for(int i=1;i<L.Count;i++){
            if(L[i] == L[i-1]){
                c++;
            }
            else{
                ans *= F.rf[c];
                ans %= Define.mod;
                c = 1;
            }
        }
        ans *= F.rf[c];
        ans %= Define.mod;
        return ans;
    }
    bool Possible(List<int> L,string S){
        int[] A = new int[L.Count];
        int Zero = L.Count-1;
        int One = L.Count-1;
        int Two = L.Count-1;
        for(int i=0;i<S.Length;i++){
            if(S[i] == 'r'){
                if(Zero != -1){
                    A[Zero]++;
                    Zero--;
                }
                else{
                    if(Two != -1 && L[Two] > 2 && A[Two] >= 2){
                        A[Two]++;
                        if(A[Two] == L[Two]){
                            Two--;
                        }
                    }
                }
            }
            else{
                if(One != -1 && L[One] != 1 && A[One] == 1){
                    A[One]++;
                    One--;
                }
                else{
                    if(Two != -1 && L[Two] > 2 && A[Two] >= 2){
                        A[Two]++;
                        if(A[Two] == L[Two]){
                            Two--;
                        }
                    }
                }
            }
        }
        return Zero == -1 && (One == -1 || L[One] == 1) && (Two == -1 || L[Two] <= 2);
    }
}
class Fact{
    public long[] f;
    public long[] rf;
    public Fact(int N){
        f = new long[N+1];
        rf = new long[N+1];
        for(int i=0;i<N+1;i++){
            if(i == 0){
                f[i] = 1;
            }
            else{
                f[i] = (f[i-1]*i)%Define.mod;
            }
        }
        for(int i=N;i>=0;i--){
            if(i == N){
                rf[i] = pow(f[N],Define.mod-2);
            }
            else{
                rf[i] = rf[i+1]*(i+1)%Define.mod;
            }
        }
    }
    public long pow(long N,long K){
        if(K == 0){
            return 1;
        }
        else if(K % 2 == 0){
            long t = pow(N,K/2);
            return t*t%Define.mod;
        }
        else{
            return N*pow(N,K-1)%Define.mod;
        }
    }
    public long GetFact(int N){
        return f[N];
    }
    public long GetPerm(int N,int R){
        return f[N] * rf[N-R] % Define.mod;
    }
    public long GetConv(int N,int R){
        return ((f[N]*rf[R])%Define.mod*rf[N-R])%Define.mod;
    }
    public long GetRev(int N){
        if(N == 0){
            return 1;
        }
        else{
            return rf[N] * f[N-1] % Define.mod;
        }
    }
}
