class Solve{
    public Solve(){}
    int[] L;
    Fact F;
    StringBuilder sb;
    ReadData re;
    long count;
    public static int Main(){
        new Solve().Run();
        return 0;
    }
    void Run(){
        sb = new StringBuilder();
        re = new ReadData();
        Calc();
        Console.Write(sb.ToString());
    }
    void Calc(){
        int N = re.i();
        int K = re.i();
        string S = re.s();
        count = 1;
        L = new int[70];
        F = new Fact(1000);
        for(int i=1;i<=70;i++){
            L[0] = i; 
            dfs(N,1,N-i,S);
        }
        count %= Define.mod;
        sb.Append(count+"\n");
    }
    void dfs(int N,int l,int n,string S){
        if(Possible(l,S)){
            count += Querry(l,N);
        }
        for(int i=L[l-1];i<=n;i++){
            L[l] = i;
            dfs(N,l+1,n-i,S);
        }
    }
    long Querry(int l,int N){
        if(l == 0){
            return 1;
        }
        int a = 0;
        int b = 0;
        for(int i=0;i<l;i++){
            if(L[i] == 1){
                a += 1;
            }
            else{
                a += L[i]*2-3;
                b += 2;
            }
        }
        a += l-1;
        b += 2;
        if(N < a){
            return 0;
        }
        long ans = F.GetConv(N+b-1,a+b-1);
        ans *= F.f[l];
        ans %= Define.mod;
        int c = 1;
        for(int i=1;i<l;i++){
            if(L[i] == L[i-1]){
                c++;
            }
            else{
                ans *= F.rf[c];
                ans %= Define.mod;
                c = 1;
            }
        }
        ans *= F.rf[c];
        ans %= Define.mod;
        return ans;
    }
    bool Possible(int l,string S){
        int[] A = new int[l];
        int Zero = l-1;
        int One = l-1;
        int Two = l-1;
        for(int i=0;i<S.Length;i++){
            if(S[i] == 'r'){
                if(Zero != -1){
                    A[Zero]++;
                    Zero--;
                }
                else{
                    if(Two != -1 && L[Two] > 2 && A[Two] >= 2){
                        A[Two]++;
                        if(A[Two] == L[Two]){
                            Two--;
                        }
                    }
                }
            }
            else{
                if(One != -1 && L[One] != 1 && A[One] == 1){
                    A[One]++;
                    One--;
                }
                else{
                    if(Two != -1 && L[Two] > 2 && A[Two] >= 2){
                        A[Two]++;
                        if(A[Two] == L[Two]){
                            Two--;
                        }
                    }
                }
            }
        }
        return Zero == -1 && (One == -1 || L[One] == 1) && (Two == -1 || L[Two] <= 2);
    }
