import std.stdio, std.algorithm, std.range, std.conv;
// import dkh.foundation, dkh.scanner;

// import dkh.modint;
alias Mint = ModInt!(10^^9 + 7);

Mint[] fact, iFac;
static this() {
    fact = factTable!Mint(1000);
    iFac = invFactTable!Mint(1000);
}

int n, m, z;
int[] v;
int r;

// import dkh.functional;

memoCont!fBase f;
Mint fBase(int pos, int sm, int cnt) {
    if (pos == -1) {
        if (sm) return Mint(0);
        int zc = m * 2 + 2;
        cnt = cnt * 2 + m;
        int oc = r + cnt + m + r - 1;
        if (n < oc) return Mint(0);
        int nn = n-oc;
        int kk = zc+oc;

        Mint u = Mint(1);
        u *= fact[nn + kk - 1];
        u *= iFac[kk - 1];
        u *= iFac[nn];
//        writeln("wow ", pos, " ", sm, " ", zc, " ", oc, " : ", u);
        return u;
    }
    Mint ans = Mint(0);
    foreach (i; 0..z+1) {
        int nsm = sm - max(0, v[pos]-i) + max(0, i-v[pos]);
        if (z < nsm) continue;
        ans += f(pos-1, max(0, nsm), cnt + i);
    }
    return ans;
}

Mint calc(int[] _v, int _r) {
    v = _v; r = _r; m = v.length.to!int;
    reverse(v);
    z = v.sum;
//    writeln("calc start: ", v, " ", r);
    f.init([[-1, m-1], [0, z], [0, 2*z]]);
    Mint res = f(v.length.to!int - 1, 0, 0);
//    writeln("res ", res);
    return res * fact[r+m] * iFac[r] * iFac[m];
}

int main() {
    Scanner sc = new Scanner(stdin);
    scope(exit) sc.read!true;

    int k; string s;
    sc.read(n, k, s);

    Mint sm = Mint(1);
    foreach (i; 0..k+1) {
        foreach (j; 0..k+1) {
            if (!i && !j) continue;
            int rc = j;
            string ns;
            foreach_reverse (c; s) {
                if (c == 'r' && rc) {
                    rc--;
                    continue;
                } else {
                    ns = c ~ ns;
                }
            }
            if (rc) continue;

            int[] v = new int[i];
            rc = 0; int bc = 0;
            foreach (c; ns) {
                if (c == 'r') {
                    if (rc != i) {
                        rc++;
                        continue;
                    }
                } else {
                    if (bc < rc) {
                        bc++;
                        continue;
                    }
                }
                if (bc == 0) continue;
                v[bc-1]++;
            }
            if (bc != i) continue;
            sm += calc(v, j);
        }
    }
    writeln(sm);
    return 0;
}
