const ll MAX=1000000007,MAXL=1LL<<61,dx[4]={-1,0,1,0},dy[4]={0,1,0,-1};
typedef pair<int,int> P;

bool dp[2][1<<24];
void Main() {
  int n;
  R n;
  n++;
  if(n>=25) {
    pr(0);
    return;
  }
  int d[n],r=0;
  d[0]=0;
  REP(i,1,n) R d[i];
  sort(d,d+n);
  dp[0][1]=1;
  REP(i,1,n) {
    rep(t,1<<24) {
      if(!dp[r][t]) continue;
      int x=d[i];
      if(!(t&(1<<x))) dp[r^1][t|(1<<x)]|=1;
      x=(24-d[i])%24;
      if(!(t&(1<<x))) dp[r^1][t|(1<<x)]|=1;
    }
    r^=1;
  }
  int ans=0;
  rep(t,1<<24) {
    if(!dp[r][t]||__builtin_popcount(t)!=n) continue;
    vector<int> v;
    rep(i,24) {
      if(t&(1<<i)) v.pb(i);
    }
    int x=MAX;
    rep(i,v.size()) {
      int y=abs(v[(i+1)%v.size()]-v[i]);
      y=min(y,24-y);
      x=min(x,y);
    }
    ans=max(ans,x);
  }
  pr(ans);
}

