bool pan(int m)
{
    for(int i = 0; i <= n; i++)
    {
        int k = a[i];
        for(int j = 0; j <= n; j++)
        {
            if(i == j)continue;
            if(a[i]+a[j] > 24)continue;
            if(a[i]+a[j]+m >= 24)return false;
        }
        k = 24-a[i];
        for(int j = 0; j <= n; j++)
        {
            if(i == j)continue;
            if(a[i]+a[j] > 24)continue;
            if(a[i]+a[j]+m >= 24)return false;
        }
    }
    return true;
}
