ll solve() {
	int n = ri();
	vector<int> A(n);
	rep(i, n) A[i] = ri();

	sort(all(A));

	if (A[0] == 0) return 0;

	vi C(13);
	forr(a, A) C[a]++;

	vi fix(25);
	vi non;

	if (C[12] >= 2) return 0;


	rep(i, 13) {
		if (C[i] >= 3) return 0;
		if (C[i] == 2) {
			fix[C[i]] = 1;
			fix[24-C[i]] = 1;
		}
		if (C[i] == 1) {
			non.emplace_back(i);
		}
	}

	out(fix);
	out(non);

	int m = sz(non);

	int ans = 0;

	rep(i, bit(m)) {
		vi T(fix);
		T[0] = T[24] = 1;

		bool ok = 1;
		rep(j, m) {
			int s = non[j];
			int t = (bit(j) & i) ? t = s : 24 - s;

			if (T[t]) {
				ok = 0;
				break;
			}
			T[t] = 1;
		}
		out(i, ok, T);
		if (ok) {
			int cand = 24;
			vi X;
			rep(x, 24) {
				if (T[x]) X.emplace_back(x);
			}
			int k = sz(X);
			rep(j, k - 1) {
				amin(cand, X[j+1] - X[j]);
			}
			amax(ans, cand);
		}
	}

	return ans;
}

