using namespace std;

/* constant */

const int T = 24;
const int H = T / 2;

/* typedef */

/* global variables */

int dns[H + 1], ds[H];
bool used[T];

/* subroutines */

inline void setmin(int &a, int b) { if (a > b) a = b; }

int check() {
  int mindf = T, prvi = 0;
  for (int i = 1; i < T; i++)
    if (used[i]) {
      setmin(mindf, i - prvi);
      prvi = i;
    }
  setmin(mindf, T - prvi);
  return mindf;
}

int rec(int m, int u) {
  if (u >= m) return check();

  used[ds[u]] = true;
  int r0 = rec(m, u + 1);
  used[ds[u]] = false;

  used[T - ds[u]] = true;
  int r1 = rec(m, u + 1);
  used[T - ds[u]] = false;

  return max(r0, r1);
}

/* main */

int main() {
  int n;
  scanf("%d", &n);

  for (int i = 0; i < n; i++) {
    int di;
    scanf("%d", &di);
    dns[di]++;
  }
  dns[0]++;

  if (dns[0] > 1 || dns[H] > 1) {
    puts("0");
    return 0;
  }

  used[0] = true;
  if (dns[H] == 1) used[H] = true;

  int m = 0;
  for (int d = 1; d < H; d++)
    switch (dns[d]) {
    case 0: break;
    case 1: ds[m++] = d; break;
    case 2: used[d] = used[T - d] = true; break;
    default: puts("0"); return 0;
    }

  int r = rec(m, 0);
  printf("%d\n", r);
  return 0;
}

