using ll = long long;

int minD(vector<int> T, ll b) {
  for (int i = 0; i < 11; i++) {
    if (T[i + 1] and (b >> i) & 1) {
      T[i + 1]--; T[24 - (i + 1)]++;
    }
  }
  int res { 24 };
  T[24]++;
  for (int i = 0; i <= 24; i++) {
    if (T[i] == 2) return 0;
    for (int j = i + 1; j <= 24; j++) {
      if (T[i] and T[j]) res = min(res, j - i);
    }
  }
  return res;
}

