    int m = 0;
    for (auto p : zip) {
        if (p.second >= 3) // 3人が同じ時刻の差であるとき，必ず2人は同じ時差に含まれるはず，よってどの組み合わせにおいても差は必ず0
            bye("0");
        repeat(i, min(2, p.second))
            dd[m++] = p.first;
    }

    n = m;
}


