	for(int now = 0;now < (1 << n);now++)
	{
		vector<int> vv;
		REP(i,n)
		{
			if(now & (1 << i))
			{
				vv.PB(24 - v[i]);
			}
			else
			{
				vv.PB(v[i]);
			}
		}

		int ret =100;
		REP(i,n)
		{
			REP(j,n)
			{
				if(i == j)continue;
				int tmp = abs(vv[i] - vv[j]);
				ret = min(ret,min(tmp,24-tmp));
			}
		}
		ans = max(ans,ret);
	}

	cout << ans << endl;

	return 0;
}

