const int MAXN = 50 + 5;

int N;
int Ans = 0;
int A[MAXN], B[MAXN], Used[MAXN];



void DFS(int depth) {
	if (depth == N + 1) {
		int C[MAXN];
		memcpy(C, B, sizeof(int) * (N + 1));
		sort(C, C + N + 1);
		int tmp = C[1] - C[0];
		for (int i = 1; i < N + 1; i++) {
			if (tmp > C[i] - C[i - 1]) {
				tmp = C[i] - C[i - 1];
			}
		}
		tmp = min(tmp, 24 - C[N]);
		if (Ans < tmp)
			Ans = tmp;
		return;
	}
	if (Used[depth])
		DFS(depth + 1);

	B[depth] = A[depth];
	DFS(depth + 1);
	B[depth] = 24 - A[depth];
	DFS(depth + 1);
	return;
}

