	for (int i = 0; i < N; i++)
		cin >> A[i];

	if (N >= 24) cout << 0 << endl;
	else {
		A[N] = 0;
		sort(A, A + N + 1);
		if (A[1] == 0)
			cout << 0 << endl;
		else {
			for (int i = 0; i < N; i++) {
				if (A[i] == A[i + 1]) {
					if (Used[i]) {
						cout << 0 << endl;
						break;
					}
					Used[i] = 1;
					Used[i + 1] = 1;
					B[i] = A[i];
					B[i + 1] = 24 - A[i];
				}
			}

			DFS(1);
			cout << Ans << endl;
		}
	}
	return 0;
}

