int dfs(int num, vector<int> v){
	if(num == 13){
		sort(v.begin(), v.end());
		int ret = 1e5;
		for(int i = 0; i < v.size() - 1; ++i) ret = min(ret, min((int)abs(v[i] - v[i + 1]), 24 - (int)abs(v[i] - v[i + 1])));
		ret = min(ret, min((int)abs(v[v.size() - 1] - v[0]), (int)abs(v[v.size() - 1] - v[0])));
		return ret;
	}
	if(!cnt[num]){
		return dfs(num + 1, v);
	}else if(cnt[num] == 2){
		v.push_back(num);
		v.push_back(24 - num);
		return dfs(num + 1, v);
	}else{
		vector<int> tmp = v;
