bool solve() {
	int n; cin >> n;
	int d[n]; REP (i,n) cin >> d[i];
	sort(d,d+n);
	int time[n]; time[0] = d[0];
	FOR (i,1,n) {
		int tmp[2];
		REP (j,2) {
			tmp[j] = INF;
			int hugo = pow(-1,j);
			int ntime = 24 + hugo * d[i]; ntime %= 24;
			REP (k,i) {
				int sa = abs(time[k]-ntime); if (sa > 12) sa = 24 - sa;
				tmp[j] = min(tmp[j],sa);
			}
		}
		if (tmp[0] < tmp[1]) {
			time[i] = 24-d[i];
		} else time[i] = d[i];
		if (i == n - 1) {
			cout << max(tmp[0],tmp[1]) << endl;
		}
	}
	// REP (i,n) cout << time[i] << endl;
	if (n == 1) cout << 0 << endl;
}

