typedef long long ll;
bool ex[24];
int cnt[13];
int main()
{
  ios::sync_with_stdio(false);
  cin.tie(0);
  int N;cin>>N;
  FOR(i,0,N){
    int d;cin>>d;
    cnt[d]++;
  }
  if(N==1){
    cout<<0<<endl;
    return 0;
  }
  if(ex[0]>=2||cnt[12]>=2){
    cout<<0<<endl;
    return 0;
  }
  ex[0]=true;
  if(cnt[12])ex[12]=true;
  vector<int> v;
  FOR(i,1,12){
    if(cnt[i]==1){
      v.push_back(i);
    }
    if(cnt[i]==2){
      ex[i]=ex[24-i]=true;
    }
    if(cnt[i]>=3){
      cout<<0<<endl;
      return 0;
    }
  }
  int ans=0;
  int n=v.size();
  FOR(i,0,1<<n){
    bool ex2[24];
    FOR(j,0,24) ex2[j]=ex[j];
    FOR(j,0,n){
      if(i&(1<<j)){
        ex2[v[j]]=true; 
      }else{
        ex2[24-v[j]]=true;
      }
    }
    int mn=1e9;
    FOR(i,0,24){
      FOR(j,i+1,24){
        if(i==j)continue;
        if(ex2[i]&&ex2[j]){
          mn=min(mn,abs(j-i));
          mn=min(mn,24-abs(j-i));
        }
      }
    }
    ans=max(ans,mn);
  }
  cout<<ans<<endl;
}

