#define REP(i,n) for(int (i)=0;(i)<(int)(n);(i)++)
#define RREP(i,n) for(int (i)=(int)(n)-1;i>=0;i--)
#define REMOVE(Itr,n) (Itr).erase(remove((Itr).begin(),(Itr).end(),n),(Itr).end())
typedef long long ll;

int main() {

    int N; cin >> N;
    vector < int > D(N);
    REP(i,N) cin >> D[i];

    if (N == 1) {
        cout << 0 << endl;
        return 0;
    }

    set < int > use;
    REP(i,D.size()) use.insert(D[i]);
    D.clear();
    for (auto x : use) D.push_back(x);

    if (D.size() == 1) {
        cout << abs(D[0] - (24 - D[0])) << endl;
        return 0;
    }

    int ans = 0;
    REP(i,(1 << 13)) {
        bool rev[13];
        REP(j,13) {
            if ((i >> j) & 1) rev[j] = false;
            else rev[j] = true;
        }
        vector < int > Dt = D;
        REP(i,Dt.size()) {
            if (!rev[Dt[i]]) Dt[i] = 24 - Dt[i];
        }
        int diff = 1e9;
        REP(j,N) REP(k,N) {
            if (j != k) {
                diff = min(diff, abs(Dt[j] - Dt[k])); 
            }
        }
        ans = max(ans, diff);
    }

    cout << ans << endl;

    return 0;
}

