int dfs(vector<int>& v, int p = 0) {
	if (p == 13) {
		int r = INT_MAX;
		rep(i, v.size()) {
			reps(j, i+1, v.size()) {
				int d = abs(v[i] - v[j]);
				chmin(r, min(d, 24 - d));
			}
		}
		return r;
	}
	if (D[p] == 0) return dfs(v, p+1);
	if (D[p] == 1) {
		v.push_back(p);
		int l = dfs(v, p+1);
		v.back() = 24-p;
		int r = dfs(v, p+1);
		v.pop_back();
		return max(l, r);
	} else {
		v.push_back(p); v.push_back(24 - p);
		int r = dfs(v, p+1);
		v.pop_back(); v.pop_back();
		return r;
	}
}


