				break;
			}
		}
	}
	sort(b, b+1+n);
	for (int i = 1; i <= n; i ++)
		for (int j = 0; j < i; j ++){
			mi = min(mi, cal(b[i], b[j]));
		}
	if (mi >= x) return true;

	mi = INF;
	b[0] = 24;
	for (int i = 1; i <= n; i ++){
		b[i] = a[i];
		for (int j = 0; j < i; j ++){
