void resolve() {
    int n;
    cin >> n;
    VI d(n);
    REP(i,0,n) cin >> d[i];
    int ans = -1, ans2 = 12;
    VI diff(13);
    REP(i,0,n) {
        if (d[i] == 12) diff[12]++;
        else diff[d[i] % 12]++;
    }
    REP(i,0,12) {
        int tmp = 12;
        if (diff[i] > 2) tmp = 0;
        else if (diff[i] >= 1) {
            if (diff[12] > 0) tmp = min(tmp, 12 - i);
            tmp = min(tmp, i);
            if (diff[i] == 2) {
                tmp = min(tmp, 24 - 2 * i);
            }
        }
        //cerr << tmp << endl;
        ans2 = min(ans2, tmp);
    }
    if (diff[12] > 1) ans2 = 0;
    dump(ans2);
    REP(b,0,1<<12) {
        int tmp = 12;
        bool bo = false;
        //cerr << tmp << endl;
        REP(i,1,12) REP(j,i+1,12) {
            if (diff[i] == 0 || diff[j] == 0) continue;
            int di = (b & (1 << i)) ? i : 24 - i;
            int dj = (b & (1 << j)) ? j : 24 - j;
            tmp = min(tmp, abs(dj - di));
            bo = true;
            //cerr << i << " " << j << " " << di << " " << dj << endl;
        }
        //cerr << tmp << endl;
        if (bo) ans = max(ans, tmp);
        //break;
    }
    cout << min(ans2, (ans > -1) ? ans : 12) << endl;
}

int main() {
    resolve();

    return 0;
}

