const int maxn = 55, maxd = 12;
int n;
int cnt[maxd + 1];

int read() {
  if (scanf("%d", &n) < 1) {
    return false;
  }
  for (int i = 0; i <= maxd; i++) {
    cnt[i] = 0;
  }
  for (int i = 0; i < n; i++) {
    int d;
    scanf("%d", &d);
    cnt[d]++;
  }
  return true;
}

void solve() {
  for (int i = 0; i <= maxd; i++) {
    if (cnt[i] >= 3) {
      printf("0\n");
      return;
    }
  }
  int res = 0;
  for (int msk = 0; msk < (1 << (maxd + 1)); msk++) {
    vector<int> ds;
    ds.push_back(0);
    for (int i = 0; i <= maxd; i++) {
      if (cnt[i] == 2) {
        ds.push_back(i);
        ds.push_back((24 - i) % 24);
      } else if (cnt[i] == 1) {
        if (msk & (1 << i)) {
          ds.push_back(i);
        } else {
          ds.push_back((24 - i) % 24);
        }
      }
    }
    int cur = inf;
    for (int i = 0; i < sz(ds); i++) {
      for (int j = i + 1; j < sz(ds); j++) {
        int d = abs(ds[j] - ds[i]);
        cur = min(cur, min(d, 24 - d));
      }
    }
    res = max(res, cur);
  }
  printf("%d\n", res);
}

