using namespace std;

void solve()
{
	int N;
	cin >> N;
	bitset<12> s;
	int ans = 0;

	for (int i = 0; i < N; i++)
	{
		int d;
		cin >> d;
		if (d)
		{
			if (s[d-1])
			{
				ans = max(ans, (12-d)*2);
			}
			s[d-1] = 1;
		}
		else
		{
			cout << 0 << endl;
			return;
		}
	}
	if (ans)
	{
		cout << ans << endl;
		return;
	}
	for (int bit = 0; bit < 1<<12; bit++)
	{
		vector<int> v;
		v.push_back(0);
		for (int i = 0; i < 12; i++)
		{
			if (!s[i]) continue;
			v.push_back((bit>>i&1)?i+1:23-i);
		}
		sort(v.begin(), v.end());
		int n = v.size(), res = min(v[0]-v[n-1]+24, v[n-1]-v[0]);
		for (int i = 0; i < n-1; i++)
		{
			int diff = v[i+1]-v[i];
			res = min(res, min(24-diff, diff));
		}
		ans = max(ans, res);
	}
	cout << ans << endl;
}

int main(void)
{
	solve();
	//cout << "yui(*-v・)yui" << endl;
	return 0;
}

